import { Box } from "@mui/material";
import React from "react";

const Image = ({ image, size = "60px" }) => {
  return (
    <Box width={size} height={size}>
      <img
        style={{ objectFit: "cover", borderRadius: "50%" }}
        width={size}
        height={size}
        alt="user"
        src={`http://127.0.0.1:27017/assets/${image}`}
      />
    </Box>
  );
};

export default Image;
